import React,{ useState, useEffect } from 'react'
import { Link,useNavigate } from 'react-router-dom';
import './Login.css'
const Login = () => {

  const history = useNavigate();

  const handleGoBack = () => {
    history.goBack();
  };

  const [isResponsive, setIsResponsive] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsResponsive(window.innerWidth <= 768); // You can adjust the breakpoint (768) as needed
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Check initial width on component mount

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);
  return (
    <section className='bod'>
        {isResponsive && (
        <div className="back-arrow responsive" onClick={handleGoBack}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M15 19l-7-7 7-7"
            />
          </svg>
        </div>
      )}
        <div className='contain'>
            <form action='#'>
                <div className='title'>
                    SignUp
                </div>
                <div className="input-box">
                    <input type = 'Text' placeholder='Username' required/>
                    <div className='underline'/>
                </div>
                <div className="input-box">
                    <input type = 'Text' placeholder='Email-ID' required/>
                    <div className='underline'/>
                </div>
                <div className="input-box">
                    <input type = 'Text' placeholder='Phone No' required/>
                    <div className='underline'/>
                </div>
                <div className="input-box">
                    <input type = 'password' placeholder='Password' required/>
                    <div className='underline'/>
                </div>
                <div className="input-box button">
                    <input type = 'submit' value="SignUp"/>
                </div>
            </form>
            <div className="crac">
                <Link to = "/Login">Already Have An Account ?</Link>
            </div>
        </div>
    </section>
  )
}

export default Login
